@Composable
fun ShowMyVisitingCardScreen() {
    val mainVM = LocalMainViewModelProvider.current
//    LockScreenOrientation(ActivityInfo.SCREEN_ORIENTATION_LANDSCAPE)
    Box(Modifier.fillMaxSize()) {
        mainVM.contact?.template_data?.svg?.trimIndent()?.let {
            SvgFromXmlStringFitScreen(
                svgXml = it, url = mainVM.qrCode.toString(), modifier = Modifier
                //            .fillMaxSize()
            )
        }


    }
}


@Composable
fun SvgFromXmlStringFitScreen(
    svgXml: String,
    url: String,
    modifier: Modifier = Modifier,
) {
    var imageBitmap by remember { mutableStateOf<ImageBitmap?>(null) }
    val configuration = LocalConfiguration.current
    val density = LocalDensity.current

    val screenWidthPx = with(density) { configuration.screenWidthDp.dp.toPx().toInt() }
    val screenHeightPx = with(density) { configuration.screenHeightDp.dp.toPx().toInt() }

    LaunchedEffect(svgXml, screenWidthPx, screenHeightPx) {
        imageBitmap = try {
            val svg = SVG.getFromString(svgXml)
            // Calculate dimensions to maintain SVG aspect ratio while filling screen
            val svgWidth = svg.documentWidth
            val svgHeight = svg.documentHeight


            val svgAspectRatio = svgWidth / svgHeight
            val screenAspectRatio = screenWidthPx.toFloat() / screenHeightPx.toFloat()
            val (bitmapWidth, bitmapHeight) = if (svgAspectRatio > screenAspectRatio) {
                // SVG is wider, fit to width
                screenWidthPx to (screenWidthPx / svgAspectRatio).toInt()
            } else {
                // SVG is taller, fit to height
                (screenHeightPx * svgAspectRatio).toInt() to screenHeightPx
            }

            svg.setDocumentWidth(bitmapWidth.toFloat())
            svg.setDocumentHeight(bitmapHeight.toFloat())
            val drawable = PictureDrawable(svg.renderToPicture())
            rotateBitmap(
                drawable.toBitmap(bitmapWidth, bitmapHeight), 90f
            ).asImageBitmap()
        } catch (e: Exception) {
            null
        }
    }

    Column(
        modifier = Modifier
            .wrapContentSize(),
        horizontalAlignment = Alignment.CenterHorizontally, verticalArrangement = Arrangement.SpaceEvenly
    ) {
        imageBitmap?.let { bitmap ->
            Image(
                bitmap = bitmap,
                contentDescription = null,
                modifier = modifier,
//                   .fillMaxSize()
                contentScale = ContentScale.Inside // Maintain aspect ratio
            )
        } ?: kotlin.run {
            CircularProgressIndicator()
        }
        QRCodeDisplay(
            text = url,
            modifier = Modifier.size(300.dp)
        )
    }
}

fun generateQrCodeBitmap(content: String, size: Int = 512): Bitmap {
    val bitMatrix: BitMatrix = MultiFormatWriter().encode(
        content,
        BarcodeFormat.QR_CODE,
        size,
        size
    )
    val bitmap = createBitmap(size, size, Bitmap.Config.RGB_565)
    for (x in 0 until size) {
        for (y in 0 until size) {
            bitmap[x, y] =
                if (bitMatrix[x, y]) android.graphics.Color.BLACK else android.graphics.Color.WHITE
        }
    }
    return bitmap
}


fun rotateBitmap(source: Bitmap, angle: Float): Bitmap {
    val matrix = Matrix().apply {
        postRotate(angle)
    }
    return Bitmap.createBitmap(source, 0, 0, source.width, source.height, matrix, true)
}


@Composable
fun QRCodeDisplay(text: String, modifier: Modifier = Modifier) {
    val qrBitmap = remember(text) { generateQrCodeBitmap(text) }
    Image(
        bitmap = qrBitmap.asImageBitmap(),
        contentDescription = "QR Code",
        modifier = modifier
    )
}

